import React, { FC, useState } from "react";
import "./styles.css";

// IMAGENS
// logo da marca
import logocrypto from "../../Images/Navbar/crypto-logo-white.png";
// logo da google play
import googleplay from "../../Images/Navbar/play-store-button.png";
// logo da app store
import appstore from "../../Images/Navbar/app-store-button.png";

// REACT-ICONS
// globo
import { IoIosGlobe } from "react-icons/io";
// seta nos links de dropdown
import { RiArrowDownSLine } from "react-icons/ri";
// hamburger menu
import { GrMenu } from "react-icons/gr";



const Navbar: FC = () => {

    /* 1º sidebar: valor || 2º setSidebar: função que altera o estado || 3º valor atual (false) */
    const [sidebar, setSidebar] = useState(false);
    /* 4º showSidebar: função a ser ativada no clique || 5º setSidebar(!sidebar): vai alterar o valor atual de false para true ou vice-versa (false-true / true-false) */
    const showSidebar = () => setSidebar(!sidebar);


    return (
        <nav>
            <div className="nav_1_div">
                <div className="nav_2_div">
                    <a href="#">
                        <img src={logocrypto} width="136px" />
                    </a>

                    <div className="nav_links">
                        <div className="nav_link_ul">
                            <div className="nav_link_li nft_li">
                                <a href="https://crypto.com/nft/">NFT</a>
                                <span className="span_new">NEW</span>
                            </div>
                            <div className="nav_link_li nav_link_margin">
                                <span>Products</span>
                                <RiArrowDownSLine />
                            </div>
                            <div className="nav_link_li nav_link_margin">
                                <span>Company</span>
                                <RiArrowDownSLine />
                            </div>
                            <div id="prices" className="nav_link_li nav_link_margin">
                                <a href="https://crypto.com/price">Prices</a>
                            </div>
                            <div id="langs" className="nav_link_li nav_link_margin">
                                <span>EN</span>
                                <RiArrowDownSLine />
                            </div>
                            <div className="nav_link_li globe_svg_div">
                                <IoIosGlobe />
                            </div>
                        </div> {/* comporta as li´s em formato de div´s */}

                        <div className="app_stores_divs">
                            <div>
                                <a href="#">
                                    <img src={googleplay} width="44px" />
                                </a>
                            </div>
                            <div>
                                <a href="#">
                                    <img src={appstore} width="44px" />
                                </a>
                            </div>
                            <div className="hamburger">
                                <GrMenu className="hamburger" />
                            </div> {/* comporta o ícone que vai ativar e desativar o menu no mobile */}
                        </div> {/* comporta os logos do google play e app store */}

                    </div> {/* comporta os li´s e os botões do google play e app store */}
                </div> {/* comporta os elementos linkáveis, tais como: logo e li´s */}
            </div> {/* adiciona uma margem para os lados e centralizar verticalmente os elem */}
        </nav>
    )
}

export default Navbar
